import React, { Component } from 'react';
import Input from '../input/Input';
import Button from '../button/Button';
import Form from '../form/Form';

import './login.css'
import axios from '../../../../../node_modules/axios/index';

export class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            user: { username:"", password:"" }
        };
    }
    handleChange(e) {
        if (e.target.type === "text") {
            this.state.user.username = e.target.value;
        
        } else {
            this.state.user.password = e.target.value;
        }
        
    }
    handleClick(e) {
        if (this.state.user.username === "") {
            return;
        } else if (this.state.user.password === "") {
            return;
        }
        axios.post('https://localhost:7140/api/Account/login', {
            method: 'POST',
            body: this.state.user,
            headers: {
                'Content-type': 'application/json'
            }
        }).then((response) => { debugger; localStorage.setItem('token', response); });

    }

  render () {
      return (
          <div className="login">
              Login Form
              <Form>
                  <Input type={"text"} placeHolder={"Username"} value={this.state.user.username} onchange={this.handleChange.bind(this)} />
                  <Input type={"password"} placeHolder={"Password"} value={this.state.user.password} onchange={this.handleChange.bind(this)} />
                  <Button onClick={this.handleClick.bind(this)} />
              </Form>
           
      </div>
    );
  }
}
